    real_t * b = (real_t *)malloc(10 * m_n * sizeof(real_t));
    real_t * c = (real_t *)malloc(10 * m_n * sizeof(real_t));
    real_t * totals = (real_t *)malloc(10 * sizeof(real_t));
    real_t temp;

